import { useEffect, useState } from 'react'
import './App.css'
import Header from './component/Header'
import axios from 'axios'

const App = () => {
    const [editMode, setEditMode] = useState(false)
    const [list, setList] = useState([])
    const [task, setTask] = useState('')
    const [dateCreated, setDateCreated] = useState('') 

    const showTodos = async () => {
        try {
            const { data } = await axios.get('/api/show/todos')
            setList(data)
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        showTodos()
    }, [])
    return (
        <>
            <Header />
            <div className="container">
                <div className="form" style={{ paddingBottom: "50px", paddingTop: "50px" }}>
                    <form>
                        <div className="form-wrapper" style={{ display: "flex", justifyContent: "space-between" }}>
                            <div style={{ flex: 1, marginRight: "10px" }}>
                                <input className="form-control" type="text" placeholder="Task" name="task"></input>
                            </div>
                            <div style={{ flex: 1 }}>
                                <input className="form-control" type="text" placeholder="Date (YYYY-MM-DD)" name="dateCreated"></input>
                            </div>
                            {
                                editMode ?
                                    <button type='submit' style={{ width: "200px", marginLeft: "10px" }} className='btn btn-primary'>Update</button>
                                    :
                                    <button type='submit' style={{ width: "200px", marginLeft: "10px" }} className='btn btn-success'>Add task</button>
                            }
                        </div>
                    </form>
                </div>

                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Task</th>
                            <th scope="col">Date</th>
                            <th scope="col">Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            list && list.map(val => (
                                <tr key={val.id} >
                                    <th scope="row">{val.id}</th>
                                    <td>{val.task}</td>
                                    <td>{val.dateCreated}</td>
                                    <td>
                                        <i  className="fa-solid fa-pen-to-square" style={{ color: "green", cursor: "pointer", marginRight: "25px" }} ></i>
                                        <i  style={{ color: "red", cursor: "pointer" }} className="fa-solid fa-trash-can"></i>
                                    </td>

                                </tr>

                            ))
                        }

                    </tbody>
                </table>
            </div>
        </>
    )
}

export default App
